import React, { useState } from 'react';
import {
  Col,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  FormText,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter
} from 'reactstrap';

import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { addPost } from '../../actions/post';

const PostForm = ({ addPost }) => {
  const [text, setText] = useState('');
  const [formData, setFormData] = useState({
    category: '',
    group: '',
    subject: '',
    message: ''
  });

  const [modal, setModal] = useState(false);

  const toggle = () => setModal(!modal);

  const topics = [
    { category_id: '1', title: 'General', url: '/general' },
    { category_id: '2', title: 'Recommendations', url: '/recommendations' },
    { category_id: '3', title: 'Carpool', url: '/carpool' },
    { category_id: '4', title: 'Lost & Found', url: '/lost_and_found' },
    { category_id: '5', title: 'Volunteer', url: '/volunteer' },
    { category_id: '6', title: 'Help Needed', url: '/help' },
    { category_id: '7', title: 'Homework', url: '/homework' }
  ];
  const all_groups = [
    { id: 'userId', group_id: '', name: 'room 34', type: 'inner' },
    { id: 'userId', group_id: '', name: 'grade 6 + room 34', type: 'outer' },
    { id: 'userId', group_id: '', name: 'sunshine', type: 'inner' },
    { id: 'userId', group_id: '', name: 'pre-k + sunshine', type: 'outer' }
  ];

  const onChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  return (
    <div>
      <div className='post-form' onClick={toggle}>
        <div className='bg-post-head p'>
          <h6>Post a message...</h6>
        </div>

        <div className='post-create-textarea'>What do you want to discuss?</div>
      </div>
      <Modal isOpen={modal} fade={false} toggle={toggle}>
        <ModalHeader toggle={toggle}> Create Post</ModalHeader>
        <ModalBody>
          <Form>
            <FormGroup tag='fieldset' row>
              <legend className='col-form-label'>Choose category</legend>
              <Col sm={10}>
                {topics.map(function(topic, index) {
                  return;
                  <FormGroup check>
                    <Label check>
                      <Input
                        type='radio'
                        name={topic.title}
                        category={topic.category_id}
                        onChange={e => onChange(e)}
                      />{' '}
                      {topic.title}
                    </Label>
                  </FormGroup>;
                })}
              </Col>
            </FormGroup>
            <FormGroup tag='fieldset' row>
              <legend className='col-form-label'>
                Choose classroom parents
              </legend>
              <Col sm={10}>
                <FormGroup check>
                  <Label check>
                    <Input type='radio' name='radio2' /> Room 34
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type='radio' name='radio2' /> Grade 6 + Room 34
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type='radio' name='radio2' /> Sunshine
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type='radio' name='radio2' /> Pre-K + Sunshine
                  </Label>
                </FormGroup>
              </Col>
            </FormGroup>
            <FormGroup row>
              <Input type='text' name='text' placeholder='Subject' />
            </FormGroup>
            <FormGroup row>
              <textarea
                name='text'
                cols='30'
                rows='5'
                placeholder='Create a post'
                value={text}
                onChange={e => setText(e.target.value)}
                required
              />
            </FormGroup>
          </Form>
        </ModalBody>
        <ModalFooter>
          <Button color='primary' onClick={toggle}>
            Post
          </Button>
        </ModalFooter>
      </Modal>
    </div>
  );
};

PostForm.propTypes = {
  addPost: PropTypes.func.isRequired
};

export default connect(null, { addPost })(PostForm);
