import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import Moment from 'react-moment';
import moment from 'moment';
import { connect } from 'react-redux';
import { deleteReminder } from '../../actions/profile';

const Reminder = ({ reminder, deleteReminder }) => {
  const reminders = education.map(edu => (
    <tr key={edu._id}>
      <td>{edu.school}</td>
      <td className="hide-sm">{edu.degree}</td>
      <td>
        <Moment format="YYYY/MM/DD">{moment.utc(edu.from)}</Moment> -{' '}
        {edu.to === null ? (
          ' Now'
        ) : (
          <Moment format="YYYY/MM/DD">{moment.utc(edu.to)}</Moment>
        )}
      </td>
      <td>
        <button
          onClick={() => deleteEducation(edu._id)}
          className="btn btn-danger"
        >
          Delete
        </button>
      </td>
    </tr>
  ));

  return (
    <Fragment>
      <h2 className="my-2">Reminders</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Event</th>
            <th className="hide-sm">Date</th>
            <th className="hide-sm">Time</th>
            <th />
          </tr>
        </thead>
        <tbody>{reminders}</tbody>
      </table>
    </Fragment>
  );
};

Education.propTypes = {
  reminder: PropTypes.array.isRequired,
  deleteReminder: PropTypes.func.isRequired
};

export default connect(
  null,
  {  }
)(Education);
