import React, { useState } from 'react';
import {
  Col,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  FormText,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter
} from 'reactstrap';
import axios from 'axios';

import Autosuggest from 'react-autosuggest';

import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { addSchool } from '../../actions/profile';

const AddSchoolForm = ({ addSchool }) => {
  const [text, setText] = useState('');
  const [school, setSchool] = React.useState('');
  const [suggestions, setSuggestions] = React.useState([]);

  const [modal, setModal] = useState(false);

  const toggle = () => setModal(!modal);

  const onSuggestionsFetchRequested = async ({ value }) => {
    if (!value) {
      setSuggestions([]);
      return;
    }

    try {
      const url = `https://api.schooldigger.com/v1.2/autocomplete/schools?q=${value}&appID=02e5e1fb&appKey=516f6dd0da01a186ffedea905bec1041`;
      const response = await axios.get(url);

      setSuggestions(
        response.schoolMatches.map(row => ({
          schoolName: row.schoolName,
          lowGrade: row.lowGrade,
          highGrade: row.highGrade,
          schoolLevel: row.schoolLevel
        }))
      );
    } catch (e) {
      setSuggestions([]);
    }
  };

  return (
    <div>
      <div className='post-form' onClick={toggle}>
        <Button color='primary' onClick={toggle}>
          Add School
        </Button>
      </div>
      <Modal isOpen={modal} fade={false} toggle={toggle}>
        <ModalHeader toggle={toggle}> Add School</ModalHeader>
        <ModalBody>
          <Form>
            <FormGroup row>
              <Col sm={10}>
                <Input
                  type='text'
                  name='text'
                  placeholder='Child Name (optional)'
                />
              </Col>
            </FormGroup>
            <FormGroup tag='fieldset' row>
              <legend className='col-form-label'>Choose category</legend>
              <Col sm={10}>
                <FormGroup check>
                  <Autosuggest
                    suggestions={suggestions}
                    onSuggestionsFetchRequested={onSuggestionsFetchRequested}
                    onSuggestionsClearRequested={() => {
                      setSuggestions([]);
                    }}
                    getSuggestionValue={suggestion => suggestion.schoolName}
                    renderSuggestion={suggestion => (
                      <div>
                        <img
                          src={suggestion.schoolLevel}
                          alt={suggestion.schoolName}
                          style={{ width: '25px' }}
                        />
                        {suggestion.schoolName}
                      </div>
                    )}
                    onSuggestionSelected={(event, { suggestion, method }) => {
                      if (method === 'enter') {
                        event.preventDefault();
                      }
                      setSchool(suggestion.schoolName);
                      //setFieldValue('country', suggestion.name);
                    }}
                    inputProps={{
                      placeholder: 'Search school name',
                      autoComplete: 'abcd',
                      value: school,
                      name: 'school',
                      onChange: (_event, { newValue }) => {
                        setSchool(newValue);
                      }
                    }}
                  />
                </FormGroup>
              </Col>
            </FormGroup>
          </Form>
        </ModalBody>
        <ModalFooter>
          <Button color='primary' onClick={toggle}>
            Add
          </Button>
        </ModalFooter>
      </Modal>
    </div>
  );
};

AddSchoolForm.propTypes = {
  addSchool: PropTypes.func.isRequired
};

export default connect(null, { addSchool })(AddSchoolForm);
