import React, { useState } from 'react';
import {
  Col,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  FormText,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter
} from 'reactstrap';
import axios from 'axios';

import Autosuggest from 'react-autosuggest';

import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { addSchool } from '../../actions/profile';

const AddSchoolForm = ({ addSchool }) => {
  const [text, setText] = useState('');
  const [country, setCountry] = React.useState('');
  const [suggestions, setSuggestions] = React.useState([]);

  const [modal, setModal] = useState(false);

  const toggle = () => setModal(!modal);

  return (
    <div>
      <div className='post-form' onClick={toggle}>
        <Button color='primary' onClick={toggle}>
          Add School
        </Button>
      </div>
      <Modal isOpen={modal} fade={false} toggle={toggle}>
        <ModalHeader toggle={toggle}> Add School</ModalHeader>
        <ModalBody>
          <Form>
            <FormGroup tag='fieldset' row>
              <legend className='col-form-label'>Choose category</legend>
              <Col sm={10}>
                <FormGroup check>
                  <Label check>
                    <Input type='radio' name='radio2' /> General
                  </Label>
                  <Autosuggest
                    suggestions={suggestions}
                    onSuggestionsFetchRequested={async ({ value }) => {
                      if (!value) {
                        setSuggestions([]);
                        return;
                      }

                      try {
                        const url = `https://api.schooldigger.com/v1.2/autocomplete/schools?q=${value}&appID=02e5e1fb&appKey=516f6dd0da01a186ffedea905bec1041`;
                        const response = await axios.get(url);

                        setSuggestions(
                          response.schoolMatches.map(row => ({
                            schoolName: row.schoolName,
                            lowGrade: row.lowGrade,
                            highGrade: row.highGrade
                          }))
                        );
                      } catch (e) {
                        setSuggestions([]);
                      }
                    }}
                    onSuggestionsClearRequested={() => {
                      setSuggestions([]);
                    }}
                    getSuggestionValue={suggestion => suggestion.schoolName}
                    renderSuggestion={suggestion => (
                      <div>
                        <img
                          src={suggestion.flag}
                          alt={suggestion.name}
                          style={{ width: '25px' }}
                        />
                        {suggestion.name}
                      </div>
                    )}
                    onSuggestionSelected={(event, { suggestion, method }) => {
                      if (method === 'enter') {
                        event.preventDefault();
                      }
                      setCountry(suggestion.name);
                      //setFieldValue('country', suggestion.name);
                    }}
                    inputProps={{
                      placeholder: 'Search for your country',
                      autoComplete: 'abcd',
                      value: country,
                      name: 'country',
                      onChange: (_event, { newValue }) => {
                        setCountry(newValue);
                      }
                    }}
                  />
                </FormGroup>
              </Col>
            </FormGroup>
            <FormGroup row>
              <Input type='text' name='text' placeholder='Subject' />
            </FormGroup>
            <FormGroup row>
              <textarea
                name='text'
                cols='30'
                rows='5'
                placeholder='Create a post'
                value={text}
                onChange={e => setText(e.target.value)}
                required
              />
            </FormGroup>
          </Form>
        </ModalBody>
        <ModalFooter>
          <Button color='primary' onClick={toggle}>
            Post
          </Button>
        </ModalFooter>
      </Modal>
    </div>
  );
};

AddSchoolForm.propTypes = {
  addSchool: PropTypes.func.isRequired
};

export default connect(null, { addSchool })(AddSchoolForm);
