import React, { useState } from 'react';
import {
  Col,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  FormText,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter
} from 'reactstrap';
import axios from 'axios';

import Autosuggest from 'react-autosuggest';

import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { addSchool } from '../../actions/profile';

const AddSchoolForm = ({ addSchool }) => {
  const [value, setValue] = useState('');
  const [school, setSchool] = React.useState('');
  const [suggestions, setSuggestions] = React.useState([]);

  const [modal, setModal] = useState(false);

  const toggle = () => setModal(!modal);

  const onChange = (event, { newValue }) => {
    setSchool(newValue);


  };

  onChange: (_event, { newValue }) => {
    setSchool(newValue);
  }

  // Autosuggest will call this function every time you need to update suggestions.
  // You already implemented this logic above, so just use it.
  const onSuggestionsFetchRequested = ({ value }) => {
    this.setState({
      suggestions: getSuggestions(value)
    });
  };

  // Autosuggest will call this function every time you need to clear suggestions.
  const onSuggestionsClearRequested = () => {
    this.setState({
      suggestions: []
    });
  };

  // Imagine you have a list of languages that you'd like to autosuggest.
  const languages = [
    {
      name: 'C',
      year: 1972
    },
    {
      name: 'Elm',
      year: 2012
    }
  ];

  // Teach Autosuggest how to calculate suggestions for any given input value.
  const getSuggestions = value => {
    const inputValue = value.trim().toLowerCase();
    const inputLength = inputValue.length;

    return inputLength === 0
      ? []
      : languages.filter(
          lang => lang.name.toLowerCase().slice(0, inputLength) === inputValue
        );
  };

  // When suggestion is clicked, Autosuggest needs to populate the input
  // based on the clicked suggestion. Teach Autosuggest how to calculate the
  // input value for every given suggestion.
  const getSuggestionValue = suggestion => suggestion.name;

  // Use your imagination to render suggestions.
  const renderSuggestion = suggestion => <div>{suggestion.name}</div>;

  // Autosuggest will pass through all these props to the input.
  const inputProps = {
    placeholder: 'Type a programming language',
    value,
    onChange: onChange
  };

  return (
    <div>
      <div className='post-form' onClick={toggle}>
        <Button color='primary' onClick={toggle}>
          Add School
        </Button>
      </div>
      <Modal isOpen={modal} fade={false} toggle={toggle}>
        <ModalHeader toggle={toggle}> Add School</ModalHeader>
        <ModalBody>
          <Form>
            <FormGroup row>
              <Col sm={10}>
                <Input
                  type='text'
                  name='text'
                  placeholder='Child Name (optional)'
                />
              </Col>
            </FormGroup>
            <FormGroup tag='fieldset' row>
              <legend className='col-form-label'>Choose category</legend>
              <Col sm={10}>
                <FormGroup check>
                  <Autosuggest
                    suggestions={suggestions}
                    onSuggestionsFetchRequested={onSuggestionsFetchRequested}
                    onSuggestionsClearRequested={onSuggestionsClearRequested}
                    getSuggestionValue={getSuggestionValue}
                    renderSuggestion={renderSuggestion}
                    inputProps={inputProps}
                  />
                </FormGroup>
              </Col>
            </FormGroup>
          </Form>
        </ModalBody>
        <ModalFooter>
          <Button color='primary' onClick={toggle}>
            Add
          </Button>
        </ModalFooter>
      </Modal>
    </div>
  );
};

AddSchoolForm.propTypes = {
  addSchool: PropTypes.func.isRequired
};

export default connect(null, { addSchool })(AddSchoolForm);
