import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { auth } from '../../../actions/auth';

import AccountModal from './modal/AccountModal';
import './Account.scss';
const Account = ({ auth }) => {
  const userData = {
    id: 1,
    name: 'Leanne Graham',
    username: 'Bret',
    email: 'Sincere@april.biz',
    address: {
      street: 'Kulas Light',
      suite: 'Apt. 556',
      city: 'Gwenborough',
      state: 'California',
      zipcode: '92998-3874',
      geo: {
        lat: '-37.3159',
        lng: '81.1496'
      }
    },
    phone: '1-770-736-8031 x56442',
    website: 'hildegard.org',
    company: {
      name: 'Romaguera-Crona',
      catchPhrase: 'Multi-layered client-server neural-net',
      bs: 'harness real-time e-markets'
    }
  };

  const userFinal = this.state.array.map((e, i) => {
    <div key={i}>{e}</div>;
  });

  const userDetails = (
    <div className='profile-content-details' key={auth.user._id}>
      <div></div>
      <div className='username'>{auth.user.name}</div>
      <hr />
      <div>{userData.name}</div>
      <div>{userData.username}</div>
      <div>{userData.email}</div>
      <div className='address'>
        {userData.address.street}, {userData.address.suite}
      </div>
      <div>
        {userData.address.city}, {userData.address.state}{' '}
        {userData.address.zipcode}
      </div>
      <div>{userData.phone}</div>
    </div>
  );

  return (
    <Fragment>
      <div className='profile-component-container'>
        <div className='profile-component-header'>
          <AccountModal />

          <h4 className='profile-component-title'>My Account</h4>
        </div>
        <div className='profile-component-content'>{userDetails}</div>
      </div>
    </Fragment>
  );
};

Account.propTypes = {
  auth: PropTypes.object.isRequired
  //deleteAccount: PropTypes.func.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(mapStateToProps, {})(Account);
